package com.aurionpro.structural.decorator.model;

public class HatDecorator implements IHat{
	public IHat decoratedHat;
	

	public HatDecorator(IHat decoratedHat) {
		super();
		this.decoratedHat = decoratedHat;
	}

	@Override
	public String getName() {
		// TODO Auto-generated method stub
		return decoratedHat.getName();
	}

	@Override
	public double getPrice() {
		// TODO Auto-generated method stub
		return decoratedHat.getPrice();
	}

	@Override
	public String getDescription() {
		// TODO Auto-generated method stub
		return decoratedHat.getDescription();
	}
	

}
